    function addTimestampToWeChat(date, time, isSystemTime, index) {
        const timeText = formatMomentTimestamp(date, time);
        const timeInMinutes = parseTimeToMinutes(timeText);

        if (isSystemTime && timeInMinutes !== null && (timeInMinutes - lastDisplayedTimeInMinutes) < 10) {
            return;
        }

        const t = document.createElement("div");
        t.className = "timestamp-row";
        if (index !== undefined) t.dataset.logIndex = index;
        t.innerHTML = `<span class="timestamp-text">${timeText}</span>`;
        wechatBody.appendChild(t);
        if (isSystemTime) {
            lastDisplayedTimeInMinutes = timeInMinutes;
        }
    }

