    function parseTimeToMinutes(e){ const t=e.match(/(\d{1,2}):(\d{2})/); return t?60*parseInt(t[1],10)+parseInt(t[2],10):null; }

    function formatMomentTimestamp(dateString, timeString) {
        if (!dateString || !timeString) return ' ';
        const postDate = new Date(dateString);
        const now = new Date(window.currentGameDate);

        const postYear = postDate.getFullYear();
        const postMonth = postDate.getMonth();
        const postDay = postDate.getDate();

        const nowYear = now.getFullYear();
        const nowMonth = now.getMonth();
        const nowDay = now.getDate();

        if (postYear === nowYear && postMonth === nowMonth && postDay === nowDay) {
            return timeString;
        }

        const yesterday = new Date(now);
        yesterday.setDate(now.getDate() - 1);
        if (postYear === yesterday.getFullYear() && postMonth === yesterday.getMonth() && postDay === yesterday.getDate()) {
            return `æ˜¨å¤© ${timeString}`;
        }

        if (postYear === nowYear) {
            return `${postMonth + 1}æœˆ${postDay}æ—¥ ${timeString}`;
        }

        return `${postYear}å¹´${postMonth + 1}æœˆ${postDay}æ—¥`;
    }

    function renderMomentsFeed() {
        if (!blmxManager) return;
        momentsFeedList.innerHTML = '';
        const posts = {}; 
        const momentPostLogIndices = [];
        blmxManager.logEntries.forEach((entry, index) => {
            if (entry.key && entry.key.includes('_MOMENT')) {
                momentPostLogIndices.push(index);
            }
        });

        momentPostLogIndices.forEach(logIndex => {
            const entry = blmxManager.logEntries[logIndex];
            posts[logIndex] = { ...entry, likes: [], comments: [], id: logIndex };
        });

        blmxManager.logEntries.forEach((entry, index) => {
            if (entry.key && (entry.key.includes('_LIKE') || entry.key.includes('_COMMENT'))) {
                const targetPostAbsoluteIndex = momentPostLogIndices[parseInt(entry.data.target_post_id, 10)];
                const targetPost = posts[targetPostAbsoluteIndex];

                if (targetPost) {
                    if (entry.key.includes('_LIKE')) {
                        const likerName = entry.key.startsWith('USER') ? getDisplayName('user') : getDisplayName('char');
                        if (!targetPost.likes.some(l => l.name === likerName)) {
                            targetPost.likes.push({ key: entry.key, name: likerName, originalLogIndex: index });
                        }
                    } else {
                        targetPost.comments.push({ ...entry, originalLogIndex: index });
                    }
                } else {
                    console.warn(`[BLMX] Could not find target post for interaction. target_post_id: ${entry.data.target_post_id}`, entry);
                }
            }
        });

        Object.values(posts).reverse().forEach(post => {
            const fromUser = post.key.startsWith('USER');
            const authorName = fromUser ? getDisplayName('user') : getDisplayName('char');
            const authorAvatar = fromUser ? AVATARS.user : AVATARS.char;
            const li = document.createElement('li'); li.className = 'moment-post'; li.dataset.postId = post.id;
            const momentSequenceId = momentPostLogIndices.indexOf(post.id);
            li.dataset.momentSequenceId = momentSequenceId;

            let mediaHtml = '';
            if (post.data.image_type === 'url' && post.data.image) mediaHtml = `<img src="${post.data.image}" class="post-media-image">`;
            else if (post.data.image_type === 'desc' && post.data.image) mediaHtml = `<div class="image-desc-content">${post.data.image}</div>`;
            let interactionsHtml = '';
            if (post.likes.length > 0 || post.comments.length > 0) {
                const likersHtml = post.likes.length > 0 ? `<div class="likes-section"><i class="fas fa-heart"></i> ${post.likes.map(l => `<span class="liker-name">${l.name}</span>`).join(', ')}</div>` : '';
                const commentsHtml = post.comments.length > 0 ? `<ul class="comments-section">${post.comments.map(c => `<li><span class="comment-author">${c.key.startsWith('USER') ? getDisplayName('user') : getDisplayName('char')}</span>: ${c.data.text}</li>`).join('')}</ul>` : '';
                interactionsHtml = `<div class="post-interactions">${likersHtml}${commentsHtml}</div>`;
            }
            const displayTime = formatMomentTimestamp(post.data.date, post.data.time);
            const deleteBtnHtml = fromUser ? `<i class="fas fa-trash-alt delete-moment-btn" title="åˆ é™¤"></i>` : '';
            li.innerHTML = `<img src="${authorAvatar}" alt="Avatar" class="post-author-avatar">
                                         <div class="post-details">
                                             <span class="post-author-name">${authorName}</span>
                                             <p class="post-content">${post.data.text || ''}</p>
                                             <div class="post-media">${mediaHtml}</div>
                                             <div class="post-meta">
                                                 <div class="post-meta-left">
                                                     <span class="timestamp">${displayTime}</span>
                                                     ${deleteBtnHtml}
                                                 </div>
                                                 <div class="post-actions">
                                                     <span class="comment-button" title="è¯„è®º/ç‚¹èµ"><i class="fas fa-comment-dots"></i></span>
                                                 </div>
                                             </div>
                                             ${interactionsHtml}
                                         </div>`;
            momentsFeedList.appendChild(li);
        });
