        const panelsContainer = document.getElementById('rpg-panels');
        if (!panelsContainer) return;
        tabsContainer.querySelectorAll('.rpg-tab').forEach(tab => tab.classList.remove('active'));
        panelsContainer.querySelectorAll('.rpg-panel').forEach(panel => panel.classList.remove('active'));
        tabElement.classList.add('active');
        const panelToShow = document.getElementById(panelId);
