            if (dateTimeInput) alert("æ ¼å¼é”™è¯¯ï¼Œè¯·è¾“å…¥ YYYY-MM-DDTHH:mm æ ¼å¼ã€‚");
            return null;
        }
        return dateTimeInput;
    }

